        // no new data available
        return content;
    }
    else if (firstPortElement == "EMPTY") {
        // "EMPTY" means that the list shall be set to empty
        portHandle.clear();
        return new Set();
    }
    else { // list shall be updated
        content = new Set();
        while (!portHandle.empty()) {
            content.add(portHandle.read());
        }
    }
    return content;
